(785, 118, '_yoast_wpseo_focuskw', 'orlo'),
(786, 118, '_yoast_wpseo_title', 'The Orlo School of Hair Design and Cosmetology | Albany, NY'),
(787, 118, '_yoast_wpseo_metadesc', 'The Orlo School of Hair Design and Cosmetology is a privately owned, independent school committed to educating up and coming beauty industry professionals.'),
(788, 118, '_yoast_wpseo_linkdex', '54'),
(789, 4, '_yoast_wpseo_focuskw', 'about'),
(790, 4, '_yoast_wpseo_title', 'About | The Orlo School of Hair Design and Cosmetology'),
(791, 4, '_yoast_wpseo_metadesc', 'At Orlo, we teach students the creative aspects of cosmetology and hair design, while also focusing on the business side of this exciting industry.'),
(792, 4, '_yoast_wpseo_linkdex', '70'),
(793, 44, '_yoast_wpseo_focuskw', 'history'),
(794, 44, '_yoast_wpseo_title', 'History | The Orlo School of Hair Design and Cosmetology'),
(795, 44, '_yoast_wpseo_linkdex', '74'),
(796, 44, '_yoast_wpseo_metadesc', 'The Orlo School of Hair Design and Cosmetology, owned and managed by the Caputo family, has been educating individuals in the Capital Region for decades.'),
(797, 46, '_yoast_wpseo_focuskw', 'accreditation'),
(798, 46, '_yoast_wpseo_title', 'Accreditation | The Orlo School of Hair Design and Cosmetology'),
(799, 46, '_yoast_wpseo_linkdex', '54'),
(800, 46, '_yoast_wpseo_metadesc', 'Orlo is accredited by the National Accrediting Commission of Career Arts and Sciences, the official accrediting agency for cosmetology schools.'),
(801, 48, '_yoast_wpseo_focuskw', 'staff'),
(802, 48, '_yoast_wpseo_title', 'Our Staff | The Orlo School of Hair Design and Cosmetology'),
(803, 48, '_yoast_wpseo_metadesc', 'The staff at Orlo teaches with years of experience in the cosmetology industry as hairdressers, estheticians, make-up artists and other roles.'),
(804, 48, '_yoast_wpseo_linkdex', '53'),
(805, 50, '_yoast_wpseo_focuskw', 'location'),
(806, 50, '_yoast_wpseo_title', 'Location | The Orlo School of Hair Design and Cosmetology'),
(807, 50, '_yoast_wpseo_metadesc', 'The Orlo School of Hair Design and Cosmetology is located at 232 North Allen Street, just one block from Central Avenue in Albany, NY.'),
(808, 50, '_yoast_wpseo_linkdex', '60'),
(809, 8, '_yoast_wpseo_focuskw', 'admissions'),
(810, 8, '_yoast_wpseo_title', 'Admissions | The Orlo School of Hair Design and Cosmetology'),
(811, 8, '_yoast_wpseo_metadesc', 'The admissions team at Orlo is prepared to answer any questions about our beauty school and can assist future students with the application process.'),
(812, 8, '_yoast_wpseo_linkdex', '81'),
(813, 15, '_yoast_wpseo_focuskw', 'apply'),
(814, 15, '_yoast_wpseo_title', 'Apply Now | The Orlo School of Hair Design and Cosmetology'),
(815, 15, '_yoast_wpseo_metadesc', 'Interested in The Orlo School of Hair Design and Cosmetology? Start the application process today by filling out our online form.'),
(816, 15, '_yoast_wpseo_linkdex', '49'),
(817, 25, '_yoast_wpseo_focuskw', 'disclosure'),
(818, 25, '_yoast_wpseo_title', 'Student Disclosure | The Orlo School of Hair Design and Cosmetology'),
(819, 25, '_yoast_wpseo_linkdex', '71'),
(820, 25, '_yoast_wpseo_metadesc', 'The consumer information on this page is provided to help potential students and their families make an informed decision while selecting a school.'),
(821, 27, '_yoast_wpseo_title', 'Tuition | Financial Aid | Orlo School of Hair Design and Cosmetology'),
(822, 27, '_yoast_wpseo_focuskw', 'tuition'),
(823, 27, '_yoast_wpseo_metadesc', 'Find information needed to plan for tuition and financial aid to attend The Orlo School of Hair Design and Cosmetology, and ask any questions you may have.'),
(824, 27, '_yoast_wpseo_linkdex', '87'),
