(830, 31, '_yoast_wpseo_title', 'Contact Admissions | The Orlo School of Hair Design and Cosmetology'),
(831, 31, '_yoast_wpseo_metadesc', 'Have questions about applying or want to schedule a visit? Contact the admissions department at The Orlo School of Hair Design and Cosmetology.'),
(832, 31, '_yoast_wpseo_linkdex', '70'),
(833, 13, '_yoast_wpseo_focuskw', 'alumni'),
(834, 13, '_yoast_wpseo_title', 'Alumni | The Orlo School of Hair Design and Cosmetology'),
(835, 13, '_yoast_wpseo_metadesc', 'We love to see our past students do well, and we encourage alumni to stay in touch with us and be an active part of our cosmetology school after graduation.'),
(836, 13, '_yoast_wpseo_linkdex', '65'),
(837, 152, '_yoast_wpseo_focuskw', 'resources'),
(838, 152, '_yoast_wpseo_title', 'Resources | The Orlo School of Hair Design and Cosmetology'),
(839, 152, '_yoast_wpseo_linkdex', '65'),
(840, 152, '_yoast_wpseo_metadesc', 'The Orlo School of Hair Design and Cosmetology created this industry resources page to be of assistance to our students, alumni, and the general public.'),
(841, 154, '_yoast_wpseo_focuskw', 'involvement'),
(842, 154, '_yoast_wpseo_title', 'Involvement | The Orlo School of Hair Design and Cosmetology'),
(843, 154, '_yoast_wpseo_metadesc', 'At the Orlo School of Hair Design and Cosmetology we want to have our alumni stay involved, this page discusses ways that our alumni can stay involved.'),
(844, 154, '_yoast_wpseo_linkdex', '49'),
(845, 156, '_yoast_wpseo_focuskw', 'career'),
(846, 156, '_yoast_wpseo_title', 'Career Placement | The Orlo School of Hair Design and Cosmetology'),
(847, 156, '_yoast_wpseo_metadesc', 'The Orlo School of Hair Design and Cosmetology loves seeing our students do well after they graduate and we can assist with career placement services.'),
(848, 156, '_yoast_wpseo_linkdex', '59'),
(853, 160, '_yoast_wpseo_focuskw', 'alumni'),
(854, 160, '_yoast_wpseo_title', 'Featured Alumni | The Orlo School of Hair Design and Cosmetology'),
(855, 160, '_yoast_wpseo_metadesc', 'Here we will feature different alumni from The Orlo School of Hair Design and Cosmetology. We are proud of our alumni and want to always show it.'),
(856, 160, '_yoast_wpseo_linkdex', '49'),
(857, 11, '_yoast_wpseo_focuskw', 'community'),
(858, 11, '_yoast_wpseo_title', 'Community | The Orlo School of Hair Design and Cosmetology'),
(859, 11, '_yoast_wpseo_metadesc', 'All of us at The Orlo School are proud to be a part of the Capital Region community and we try to get involved in as many regional activities as we can.'),
(860, 11, '_yoast_wpseo_linkdex', '74'),
(861, 137, '_yoast_wpseo_focuskw', 'local'),
(862, 137, '_yoast_wpseo_title', 'Local Involvement | The Orlo School of Hair Design and Cosmetology'),
(863, 137, '_yoast_wpseo_metadesc', 'At The Orlo School we are proud citizens of the Capital Region and we love to be involved in local activites including fundraisers, fashion shows, and more.'),
(864, 137, '_yoast_wpseo_linkdex', '72'),
(865, 140, '_yoast_wpseo_focuskw', 'services'),
(866, 140, '_yoast_wpseo_title', 'Student Salon Services | Orlo | Albany, NY'),
(867, 140, '_yoast_wpseo_metadesc', 'Orlo offers salon services to Capital Region clients done by our advanced students, who are supervised by our licensed instructors at all times.'),
(868, 140, '_yoast_wpseo_linkdex', '83'),
(869, 142, '_yoast_wpseo_focuskw', 'city'),
(870, 142, '_yoast_wpseo_title', 'City Highlights | The Orlo School of Hair Design and Cosmetology'),
(871, 142, '_yoast_wpseo_metadesc', 'The Capital Region has all kinds of things to do, including special events and festivities year round, here are just some of the things to do in the area.'),
(872, 142, '_yoast_wpseo_linkdex', '75'),
(873, 82, '_yoast_wpseo_focuskw', 'calendar'),
