(901, 6, '_yoast_wpseo_focuskw', 'program'),
(902, 6, '_yoast_wpseo_title', 'Program | Courses | The Orlo School of Hair Design and Cosmetology'),
(903, 6, '_yoast_wpseo_metadesc', 'The Orlo School curriculum is designed to prepare students to earn their cosmetology licenses and help them begin their careers within our industry.'),
